--
-- Name: v_auditor_stats; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.v_auditor_stats AS
 SELECT count(*) AS total_audits,
    count(
        CASE
            WHEN ((approval_status)::text = 'approved'::text) THEN 1
            ELSE NULL::integer
        END) AS approved_count,
    count(
        CASE
            WHEN ((approval_status)::text = 'needs_revision'::text) THEN 1
            ELSE NULL::integer
        END) AS revision_count,
    count(
        CASE
            WHEN ((approval_status)::text = 'rejected'::text) THEN 1
            ELSE NULL::integer
        END) AS rejected_count,
    round(avg(average_score), 2) AS avg_score,
    round(avg(completeness_score), 2) AS avg_completeness,
    round(avg(clarity_score), 2) AS avg_clarity,
    round(avg(feasibility_score), 2) AS avg_feasibility,
    round(avg(innovation_score), 2) AS avg_innovation,
    round(avg(quality_score), 2) AS avg_quality,
    round((((count(
        CASE
            WHEN ((approval_status)::text = 'approved'::text) THEN 1
            ELSE NULL::integer
        END))::numeric * 100.0) / (NULLIF(count(*), 0))::numeric), 1) AS approval_rate_pct
   FROM public.auditor_results
  WHERE (created_at >= (CURRENT_DATE - '30 days'::interval));


