CREATE VIEW public.v_planner_stats AS
 SELECT count(*) AS total_plans,
    count(
        CASE
            WHEN (data_mapping_complete = true) THEN 1
            ELSE NULL::integer
        END) AS complete_mappings,
    round(avg(sections_count), 1) AS avg_sections,
    round(avg(total_word_count_target), 0) AS avg_word_target,
    template_name,
    count(*) AS template_usage_count
   FROM public.planner_structures
  WHERE (created_at >= (CURRENT_DATE - '30 days'::interval))
  GROUP BY template_name;


