--
-- Name: v_plans_incomplete_data; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.v_plans_incomplete_data AS
 SELECT ps.id,
    s.anketa_id,
    u.username,
    ps.missing_data_sections,
    ps.sections_count,
    jsonb_array_length(ps.missing_data_sections) AS missing_sections_count,
    ps.created_at
   FROM ((public.planner_structures ps
     JOIN public.sessions s ON ((ps.session_id = s.id)))
     LEFT JOIN public.users u ON ((s.telegram_id = u.telegram_id)))
  WHERE ((ps.data_mapping_complete = false) AND (ps.missing_data_sections IS NOT NULL))
  ORDER BY ps.created_at DESC;


